\":{\"tag\":\"share_1\",\"bdSize\":32,\"bdCustomStyle\":\"http://ats.taiwan.cn/homepage2017/css/bgBdShare2017.css\"}};with(document)0[(getElementsByTagName('head')[0]||body).appendChild(createElement('script')).src='http://bdimg.share.baidu.com/static/api/js/share.js?v=89860593.js?cdnversion='+~(-new Date()/36e5)];</script>\n\t<!-- Baidu Button 2014 END -->\n  </div>\n</div>\n<div id=\"bdShareBtn\"></div>\n    </div>\n    <!--baidu\u5206\u4eab_end-->\n  </div>  \n  <div id=\"contentArea\">\n    <div class=TRS_Editor><p align=\"justify\">\u3000\u3000\u4e2d\u56fd\u53f0\u6e7e\u7f5111\u67082\u65e5\u8baf \u636e\u53f0\u6e7e\u300a\u4e2d\u56fd\u65f6\u62a5\u300b\u62a5\u9053\uff0c\u53f0\u5f53\u5c40\u201c\u884c\u653f\u9662\u4e3b\u8ba1\u5904\u201d\u65e5\u524d\u516c\u5e03\u53bb\u5e74\u5c9b\u5185\u9752\u5e74\u5e73\u5747\u85aa\u8d44\u6240\u5f97\uff0c\u79f0\u672a\u6ee135\u5c81\u9752\u5e74\u5e73\u5747\u5e74\u6240\u5f9754.37\u4e07\u5143\uff08\u65b0\u53f0\u5e01\uff0c\u4e0b\u540c\uff09\uff0c\u521b\u5386\u53f2\u65b0\u9ad8\u3002\u56fd\u6c11\u515a\u4e2d\u5e38\u59d4\u7eb7\u7eb7\u8d28\u7591\u201c\u81a8\u98ce\u201d\uff08\u5439\u725b\uff0c\u8bf4\u5927\u8bdd\uff09\uff0c\u515a\u4e3b\u5e2d\u5434\u6566\u4e49\u8ba4\u4e3a\u662f\u201c\u9b3c\u8bdd\u201d\uff0c\u6307\u793a\u6587\u4f20\u4f1a\u53ca\u201c\u7acb\u9662\u201d\u515a\u56e2\u5217\u5165\u6279\u5224\u91cd\u70b9\uff0c\u5e76\u5236\u4f5c\u5f71\u7247\u6587\u5ba3\uff0c\u5168\u9762\u5524\u8d77\u9752\u5e74\u76f8\u5bf9\u88ab\u5265\u593a\u611f\u3002</p>\n<p align=\"justify\">\u3000\u3000\u53f0\u5f53\u5c40\u201c\u4e3b\u8ba1\u603b\u5904\u201d\u7edf\u8ba1\uff0c\u53bb\u5e74\u672a\u6ee130\u5c81\u5e73\u5747\u5e74\u6240\u5f9747.28\u4e07\u5143\uff0c\u5e74\u589e5155\u5143\u300230\u81f334\u5c81\u5e73\u5747\u5e74\u6240\u5f9763.86\u4e07\u5143\uff0c\u5e74\u589e3\u4e074566\u5143\u3002\u603b\u8ba1\u672a\u6ee135\u5c81\u8005\u5e73\u5747\u5e74\u6240\u5f9754.37\u4e07\u5143\uff0c\u5747\u521b\u65b0\u9ad8\u3002\u56fd\u6c11\u515a\u4e2d\u5e38\u4f1a\u9752\u5de5\u603b\u4f1a\u957f\u6797\u674f\u513f\u3001\u9752\u5e74\u56e2\u603b\u56e2\u957f\u7530\u65b9\u4f26\u53ca\u5de5\u4f1a\u4ee3\u8868\u59da\u6c5f\u4e34\u7b49\u7eb7\u7eb7\u6279\u201c\u81a8\u98ce\u201d\u3002\u6709\u4eba\u8bf4\uff0c\u8981\u53d1\u52a8\u9752\u5e74\u5230\u8521\u529e\u5411\u8521\u82f1\u6587\u8ba8\u85aa\u6c34\u3002</p>\n<p align=\"justify\">\u3000\u3000\u7530\u65b9\u4f26\u65e5\u524d\u5728\u9996\u573a\u201c\u80a5\u7682\u7bb1\u6f14\u8bb2\u201d\u516c\u5f00\u201c\u9053\u6b49\u201d\u8bf4\uff0c\u4ed6\u4eec\u62c9\u4f4e\u5168\u53f0\u5e74\u8f7b\u4eba\u85aa\u8d44\u5e73\u5747\u6570\uff0c\u539f\u56e0\u662f\u62ff\u9519\u515a\u8bc1\uff0c\u4e0d\u50cf\u201c\u53f0\u8239\u73af\u6d77\u201d\u8463\u5ea7\u90d1\u4ea6\u9e9f\u6708\u98869\u4e07\uff0c\u6c11\u8fdb\u515a\u526f\u79d8\u4e66\u957f\u6797\u98de\u5e06\u3001\u201c\u53e3\u8bd1\u54e5\u201d\u8d75\u6021\u7fd4\u6708\u85aa28\u4e07\u63d0\u9ad8\u5e74\u8f7b\u4eba\u5e73\u5747\u85aa\u8d44\uff0c\u751a\u81f3\u4e70\u70df\u6bd4\u8f83\u8d35\u56e0\u6ca1\u7ba1\u9053\u4e70\u514d\u7a0e\u70df\u3002</p>\n<p align=\"justify\">\u3000\u3000\u201c\u84dd\u59d4\u201d\u8bb8\u6bd3\u4ec1\u8d28\u7591\uff0c\u9009\u4e3e\u4e00\u5230\u8521\u5f53\u5c40\u53c8\u201c\u81a8\u98ce\u201d\uff0c\u8425\u9020\u767e\u4e1a\u5174\u76db\u5047\u8c61\uff0c\u5b9e\u9645\u4e00\u5806\u5e97\u9762\u79df\u4e0d\u51fa\u53bb\uff0c\u7ecf\u6d4e\u4e0d\u4f73\u72b6\u51b5\u6c11\u4f17\u6700\u6709\u611f\u3002\u82e5\u5e73\u5747\u5e74\u6536\u516554.37\u4e07\u5143\uff0c\u7b49\u4e8e\u6bcf\u4eba\u6708\u6536\u5165\u8d85\u8fc74.5\u4e07\u5143\uff0c\u53ef\u80fd\u5417\uff1f\u5c3d\u7ba1\u53f0\u5f53\u5c40\u6307\u85aa\u8d44\u521b\u65b0\u9ad8\uff0c\u4f46\u6839\u636e1111\u4eba\u529b\u94f6\u884c\u8c03\u67e5\u663e\u793a\uff0c90\u4e16\u4ee3(1990\u5e74\u540e\u51fa\u751f)\u3001\u8fdb\u5165\u793e\u4f1a\u5de5\u4f5c\u7684\u4e0a\u73ed\u65cf\uff0c\u9ad8\u8fbe59%\u201c\u4e0d\u6ee1\u610f\u201d\u85aa\u8d44\u5f85\u9047\uff0c\u4ec54.7 %\u6ee1\u610f\u3002</p>\n<p align=\"justify\">\u3000\u3000\u5b66\u8005\u8bf4\uff0c\u4ee5\u5e73\u5747\u5e74\u6240\u5f97\u505a\u653f\u7ee9\uff0c\u6700\u5927\u95ee\u9898\u662f\uff0c\u53ea\u8981\u67091%\u85aa\u8d44\u9ad8\u5f97\u5413\u4eba\uff0c\u8db3\u4ee5\u5de6\u53f3\u5e73\u5747\u6570\u636e\u7ed3\u679c\uff0c\u8fd9\u79cd\u4e13\u6311\u597d\u770b\u6570\u5b57\u505a\u5ba3\u4f20\uff0c\u96be\u602a\u591a\u6570\u6c11\u4f17\u65e0\u611f\u3002\uff08\u4e2d\u56fd\u53f0\u6e7e\u7f51 \u674e\u5b81\uff09</p></div>\n  </div>\n  <p class=\"editor\">[\u8d23\u4efb\u7f16\u8f91\uff1a\u674e\u5b81]</p>\n  <!--\u7ffb\u9875\u9875\u7801_begin-->\n  <div id=\"pageNumber\">\n    <script type=\"text/javascript\">\n      createPageHTML(1, 0, \"t20191102_12214663\", \"htm\");\n    </script>\n  </div>\n  <!--\u7ffb\u9875\u9875\u7801_end-->\n  <!--\u76f8\u5173\u5185\u5bb9_begin-->\n  <div id=\"relatedInfo\">\n    <h3>\u76f8\u5173\u5185\u5bb9</h3>\n    <ul>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/taiwan/jsxw/201911/t20191102_12214633.htm\" target=\"_blank\" title=\"\u53f0\u8b66\u5bdf\u88ab\u5224\u8d54\u201c\u592a\u9633\u82b1\u5b66\u8fd0\u201d\u767e\u4e07 \u906d\u8f70\u201c\u653f\u6cbb\u6027\u5224\u51b3\u201d\">\u53f0\u8b66\u5bdf\u88ab\u5224\u8d54\u201c\u592a\u9633\u82b1\u5b66\u8fd0\u201d\u767e\u4e07 \u906d\u8f70\u201c\u653f\u6cbb\u6027\u5224\u51b3\u201d</a></li>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/taiwan/jsxw/201911/t20191102_12214626.htm\" target=\"_blank\" title=\"\u5168\u53f01/3\u53bf\u5e02\u5730\u4e0b\u6c34\u6ca6\u4e3a\u810f\u6c34 \u65b0\u7af9\u53f0\u5357\u9ad8\u96c4\u6c34\u6c61\u67d3\u6700\u4e25\u91cd\">\u5168\u53f01/3\u53bf\u5e02\u5730\u4e0b\u6c34\u6ca6\u4e3a\u810f\u6c34 \u65b0\u7af9\u53f0\u5357\u9ad8\u96c4\u6c34\u6c61\u67d3\u6700\u4e25\u91cd</a></li>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/taiwan/jsxw/201911/t20191102_12214625.htm\" target=\"_blank\" title=\"\u8521\u82f1\u6587\u81ea\u521b\u6bd4\u8f83\u65b9\u6cd5\u79f0\u53f0\u6e7eGDP&quot;\u4e9a\u6d32\u56db\u5c0f\u9f99&quot;\u7b2c\u4e00 \u906d\u545b\u5047\u65b0\u95fb\">\u8521\u82f1\u6587\u81ea\u521b\u6bd4\u8f83\u65b9\u6cd5\u79f0\u53f0\u6e7eGDP\"\