ey\":{},\"bdText\":\"\",\"bdMini\":\"2\",\"bdMiniList\":false,\"bdPic\":\"\",\"bdStyle\":\"0\",\"bdSize\":\"16\"},\"share\":{\"tag\":\"share_1\",\"bdSize\":32,\"bdCustomStyle\":\"http://ats.taiwan.cn/homepage2017/css/bgBdShare2017.css\"}};with(document)0[(getElementsByTagName('head')[0]||body).appendChild(createElement('script')).src='http://bdimg.share.baidu.com/static/api/js/share.js?v=89860593.js?cdnversion='+~(-new Date()/36e5)];</script>\n\t<!-- Baidu Button 2014 END -->\n  </div>\n</div>\n<div id=\"bdShareBtn\"></div>\n    </div>\n    <!--baidu\u5206\u4eab_end-->\n  </div>  \n  <div id=\"contentArea\">\n    <div class=TRS_Editor><p align=\"center\"><img style=\"border-left-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-top-width: 0px\" alt=\"\" oldsrc=\"W020191008542201648255.jpg\" src=\"./W020191008542201648255.jpg\" /></p>\n<p align=\"center\"><font face=\"\u6977\u4f53,\u6977\u4f53_GB2312\">\u524d\u201c\u7acb\u59d4\u201d\u6c88\u5bcc\u96c4\u3002\uff08\u56fe\u7247\u6765\u6e90\uff1a\u53f0\u6e7e\u201c\u4e2d\u65f6\u7535\u5b50\u62a5\u201d\uff09</font></p>\n<p align=\"justify\">\u3000\u3000\u4e2d\u56fd\u53f0\u6e7e\u7f5110\u67088\u65e5\u8baf \u636e\u53f0\u6e7e\u201c\u4e2d\u65f6\u7535\u5b50\u62a5\u201d\u62a5\u9053\uff0c\u524d\u201c\u7acb\u6cd5\u59d4\u5458\u201d\u6c88\u5bcc\u96c4\u5728\u8138\u4e66\uff08Facebook\uff09\u4e0a\u9884\u6d4b2020\u9009\u4e3e\u7ed3\u679c\uff0c\u8868\u793a\u8521\u82f1\u6587\u5c06\u5927\u80dc\u9ad8\u96c4\u5e02\u957f\u97e9\u56fd\u745c125\u4e07\u7968\uff0c\u6210\u529f\u8fde\u4efb\uff1b\u6c11\u8fdb\u515a\u4e5f\u5c06\u4e00\u4e3e\u593a\u4e0b56\u5e2d\u201c\u7acb\u59d4\u201d\uff1b\u867d\u7136\u6587\u7ae0\u4e2d\u6ce8\u660e\u4e86\u201cJust for fun\u201d\u8868\u793a\u53ea\u662f\u731c\u6d4b\uff0c\u4f46\u8fd8\u662f\u5f15\u6765\u4e0d\u5c11\u5410\u69fd\u3002</p>\n<p align=\"justify\">\u3000\u3000\u81ea\u7531\u4f5c\u5bb6\u6d1b\u6749\u57fa\u5c31\u5728\u8138\u4e66\u4e0a\u8868\u793a\uff0c\u6c88\u5bcc\u96c4\u4e00\u5411\u559c\u6b22\u9884\u6d4b\u9009\u60c5\uff0c\u4f46\u662f\u731c\u4e2d\u7684\u673a\u7387\u59cb\u7ec8\u4e0d\u9ad8\uff0c\u5bf9\u4e8e\u6c88\u7684\u731c\u6d4b\uff0c\u6d1b\u6749\u57fa\u4e5f\u63d0\u51fa\u4e86\u51e0\u70b9\u5206\u6790\uff1a</p>\n<p align=\"justify\">\u3000\u3000\u7b2c\u4e00\uff0c\u5982\u679c\u8521\u82f1\u6587\u56e2\u961f\u771f\u7684\u53ef\u4ee5\u5728\u660e\u5e74\u5927\u80dc\u97e9\u56fd\u745c125\u4e07\u7968\uff0c\u90a3\u4e48\u4ed6\u4eec\u6839\u672c\u5c31\u53ef\u4ee5\u201c\u8fb9\u559d\u7ea2\u9152\u8eba\u7740\u9009\u201d\uff0c\u4f55\u82e6\u52a8\u5458\u5f53\u5c40\u5404\u90e8\u95e8\u3001\u540d\u5634\u30011450\u7684\u529b\u91cf\uff0c\u94fa\u5929\u76d6\u5730\u7684\u62b9\u9ed1\u97e9\u56fd\u745c\uff1f</p>\n<p align=\"justify\">\u3000\u3000\u7b2c\u4e8c\uff0c\u5982\u679c\u8521\u82f1\u6587\u9009\u60c5\u771f\u7684\u5982\u6b64\u4e50\u89c2\uff0c\u8868\u793a\u9009\u6c11\u57282018\u5e74\u5e95\u7684\u9009\u4e3e\u7ed3\u679c\u5df2\u7ecf\u201c\u5f7b\u5e95\u6559\u8bad\u201d\u8521\u5f53\u5c40\uff0c\u7136\u540e\u5b8c\u5168\u539f\u8c05\u4e86\u5417\uff1f\u6c88\u5bcc\u96c4\u96be\u9053\u8ba4\u4e3a\u53f0\u6e7e\u7684\u6c11\u4f17\u90fd\u662f\u91d1\u9c7c\u8111\u888b\uff0c\u5bf9\u4e8e\u8521\u5f53\u5c40\u7684\u6240\u4f5c\u6240\u4e3a\u90fd\u5fd8\u5149\u4e86\u5417\uff1f</p>\n<p align=\"justify\">\u3000\u3000\u7b2c\u4e09\uff0c\u8521\u5f53\u5c40\u7684\u916c\u5eb8\u80a5\u732b\u6570\u90fd\u6570\u4e0d\u6e05\uff0c\u8fd8\u6709\u201c\u7acb\u59d4\u201d\u56e0\u6b64\u906d\u5230\u5224\u5211\uff0c\u660e\u5e74\u6c11\u8fdb\u515a\u201c\u7acb\u59d4\u201d\u4f9d\u65e7\u53ef\u4ee5\u8fc7\u534a\uff1f\u6c88\u5bcc\u96c4\u771f\u7684\u8ba4\u4e3a\u53f0\u6e7e\u6c11\u4f17\u53ef\u4ee5\u5bb9\u5fcd\u8d70\u79c1\u70df\u3001300\u4e07\u3001\u65ad\u6865\u60e8\u6848\u3001\u8bba\u6587\u6848\uff0c\u5e76\u4e14\u7ee7\u7eed\u575a\u5b9a\u4e0d\u79fb\u7684\u4fe1\u4ef0\u6c11\u8fdb\u515a\uff1f</p>\n<p align=\"justify\">\u3000\u3000\u6d1b\u6749\u57fa\u8868\u793a\uff0c\u4ed6\u6562\u8ddf\u6c88\u5bcc\u96c4\u5bf9\u8d4c\u767e\u4efd\u9e21\u6392\uff0c\u6c88\u7684\u9884\u6d4b\u80af\u5b9a\u662f\u9519\u8bef\u7684\uff0c\u201c\u53cd\u6b63\u662fJust for fun\u201d\u3002\uff08\u4e2d\u56fd\u53f0\u6e7e\u7f51 \u674e\u5955\u5747\uff09</p></div>\n  </div>\n  <p class=\"editor\">[\u8d23\u4efb\u7f16\u8f91\uff1a\u674e\u5955\u5747]</p>\n  <!--\u7ffb\u9875\u9875\u7801_begin-->\n  <div id=\"pageNumber\">\n    <script type=\"text/javascript\">\n      createPageHTML(1, 0, \"t20191008_12207784\", \"htm\");\n    </script>\n  </div>\n  <!--\u7ffb\u9875\u9875\u7801_end-->\n  <!--\u76f8\u5173\u5185\u5bb9_begin-->\n  <div id=\"relatedInfo\">\n    <h3>\u76f8\u5173\u5185\u5bb9</h3>\n    <ul>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/plzhx/wyrt/201909/t20190927_12205803.htm\" target=\"_blank\" title=\"\u5728\u4e00\u7247\u6b22\u6b23\u9f13\u821e\u7684\u56fd\u5e86\u8282\u65e5\u6c1b\u56f4\u4e2d\uff0c\u53f0\u6e7e\u65e0\u6bd4\u9eef\u6de1\">\u5728\u4e00\u7247\u6b22\u6b23\u9f13\u821e\u7684\u56fd\u5e86\u8282\u65e5\u6c1b\u56f4\u4e2d\uff0c\u53f0\u6e7e\u65e0\u6bd4\u9eef\u6de1</a></li>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/plzhx/wyrt/201909/t20190926_12205589.htm\" target=\"_blank\" title=\"\u53f0\u6e7e\u7ecf\u6d4e\u662f\u53d8\u597d\u4e86\u8fd8\u662f\u53d8\u574f\u4e86\uff1f\u8521\u5f53\u5c40\u7684\u7a7a\u8bdd\u60e8\u906d\u6253\u8138\">\u53f0\u6e7e\u7ecf\u6d4e\u662f\u53d8\u597d\u4e86\u8fd8\u662f\u53d8\u574f\u4e86\uff1f\u8521\u5f53\u5c40\u7684\u7a7a\u8bdd\u60e8\u906d\u6253\u8138</a></li>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/plzhx/wyrt/201909/t20190926_12205586.htm\" target=\"_blank\" title=\"\u53f0\u6e7e\u6c11\u4f17\u5bf9\u201c\u53cb\u90a6\u65ad\u4ea4\u201d\u5df2\u7ecf\u9ebb\u6728\u4e86\uff1f\">\u53f0\u6e7e\u6c11\u4f17\u5bf9\u201c\u53cb\u90a6\u65ad\u4ea4\u201d\u5df2\u7ecf\u9ebb\u6728\u4e86\uff1f</a></li>\n\t \n\t  <li><a href=\"http://www.zhongguotongcuhui.org.cn/hxsp/201909/t20190925_12204982.html\" target=\"_blank\" title=\"\u53f0\u6e7e\u63a5\u8fde\u88ab\u201c\u90a6\u4ea4\u56fd\u201d\u629b\u5f03\uff0c\u663e\u793a\u201c\u53f0\u72ec\u201d\u5728\u56fd\u9645\u4e0