_config={\"common\":{\"bdSnsKey\":{},\"bdText\":\"\",\"bdMini\":\"2\",\"bdMiniList\":false,\"bdPic\":\"\",\"bdStyle\":\"0\",\"bdSize\":\"16\"},\"share\":{\"tag\":\"share_1\",\"bdSize\":32,\"bdCustomStyle\":\"http://ats.taiwan.cn/homepage2017/css/bgBdShare2017.css\"}};with(document)0[(getElementsByTagName('head')[0]||body).appendChild(createElement('script')).src='http://bdimg.share.baidu.com/static/api/js/share.js?v=89860593.js?cdnversion='+~(-new Date()/36e5)];</script>\n\t<!-- Baidu Button 2014 END -->\n  </div>\n</div>\n<div id=\"bdShareBtn\"></div>\n    </div>\n    <!--baidu\u5206\u4eab_end-->\n  </div>  \n  <div id=\"contentArea\">\n    <div class=TRS_Editor><p align=\"justify\"><b>\u3010\u4e24\u5cb8\u5feb\u8bc4\u7b2c336\u671f\u3011</b></p>\n<p align=\"justify\">\u3000\u3000\u8521\u82f1\u6587\u535a\u58eb\u8bba\u6587\u7684\u771f\u4f2a\u4e00\u76f4\u5907\u53d7\u8d28\u7591\uff0c\u5c24\u5176\u8fd1\u6765\uff0c\u6df1\u7eff\u4eba\u58eb\u76f8\u7ee7\u6380\u8d77\u6570\u8f6e\u8d28\u7591\u8521\u82f1\u201c\u6587\u201d\u3001\u5bfb\u627e\u8521\u82f1\u201c\u6587\u201d\u7684\u653b\u52bf\uff0c\u917f\u6210\u5c9b\u5185\u5916\u77a9\u76ee\u201c\u8bba\u6587\u95e8\u201d\u4e8b\u4ef6\uff0c\u66f4\u4f7f\u8fd9\u4f4d\u53f0\u6e7e\u5730\u533a\u9886\u5bfc\u4eba\u7684\u70ed\u5ea6\u53ea\u589e\u4e0d\u51cf\u3002\u62ff\u51fa\u8bc1\u4e66\u3001\u79c0\u51fa\u8bba\u6587\uff0c\u5982\u6b64\u4e00\u7ffb\u4e00\u770b\u7686\u6d1e\u89c1\u7684\u7b80\u5355\u64cd\u4f5c\uff0c\u5c31\u80fd\u4ee4\u4f17\u4eba\u201c\u95fb\u9999\u201d\u8bc6\u4eba\uff0c\u672a\u6599\u5230\u5374\u5bfc\u5411\u66f4\u4e3a\u8be1\u5f02\u7684\u6781\u7aef\u2014\u2014\u4e8b\u4ef6\u6f84\u6e05\u5374\u4e0d\u6e05\uff0c\u8bba\u6587\u8bc1\u5b9e\u5374\u4e0d\u5b9e\u3002</p>\n<p align=\"justify\">\u3000\u30009\u670823\u65e5\uff0c\u8521\u5f53\u5c40\u7684\u201c\u5e9c\u201d\u53d1\u8a00\u4eba\u4eec\u201c\u5343\u547c\u4e07\u5524\u59cb\u51fa\u6765\u201d\uff0c\u624b\u6234\u767d\u624b\u5957\uff0c\u715e\u6709\u4ecb\u4e8b\u5730\u5c06\u8521\u82f1\u6587\u535a\u58eb\u7684\u6240\u8c13\u201c\u8bba\u6587\u201d\u4e00\u9875\u9875\u793a\u4e8e\u4eba\u524d\uff0c\u4e14\u201c\u6709\u7406\u6709\u636e\u201d\u5730\u9488\u5bf96\u4e2a\u4e89\u8bae\u505a\u51fa6\u70b9\u56de\u5e94\u3002\u7136\u800c\uff0c\u8fd9\u79cd\u4f01\u56fe\u4e00\u6b21\u201c\u6d17\u767d\u201d\u76f8\u5173\u4e89\u8bae\u7684\u505a\u6cd5\u4ecd\u65e7\u7559\u4e0b\u91cd\u91cd\u7591\u70b9\uff0c\u4ee4\u4eba\u66f4\u542f\u7591\u7aa6\u3002</p>\n<p align=\"justify\"><strong>\u3000\u3000\u65b0\u4e00\u8f6e\u7684\u771f\u4f2a\u95ee\u9898\uff1a\u88ab\u5c0f\u5fc3\u7ffc\u7ffc\u5c55\u793a\u7684\u201c\u8bba\u6587\u201d\u662f\u771f\u8bba\u6587\u5417\uff1f</strong></p>\n<p align=\"justify\">\u3000\u3000\u88ab\u642c\u51fa\u7684\u8521\u201c\u535a\u58eb\u201d\u8bba\u6587\u539f\u7a3f\uff0c\u975e\u88c5\u8ba2\u6210\u518c\uff0c\u800c\u662f\u4e00\u5f20\u5f20A4\u5c3a\u5bf8\u7684\u6253\u5b57\u7a3f\uff0c\u5df2\u5448\u73b0\u6cdb\u9ec4\u3002\u7136\u800c\uff0c\u8fd9\u4ecd\u96be\u670d\u4f17\u53e3\u3002\u4f17\u6240\u5468\u77e5\uff0c\u535a\u58eb\u8bba\u6587\u5e94\u8be5\u662f\u201c\u4e00\u672c\u201d\uff0c\u800c\u975e\u201c\u4e00\u9875\u4e00\u9875\u201d\uff0c\u56e0\u6b64\u5c31\u89c6\u89c9\u611f\u53d7\u770b\uff0c\u8fd9\u66f4\u50cf\u662f\u8521\u82f1\u6587\u5f53\u5e74\u7684\u4e2a\u4eba\u7b14\u8bb0\u6216\u8005\u7eb8\u8d28\u8349\u7a3f\u3002\u53f0\u6e7e\u5e08\u8303\u5927\u5b66\u9000\u4f11\u6559\u6388\u6f58\u671d\u9633\u8868\u793a\uff0c\u201c\u8bba\u6587\u539f\u7a3f\u540e\u9762\u7684\u53d1\u5c55\uff0c\u4e5f\u8bb8\u8fc7\u5173\uff0c\u4e5f\u8bb8\u6ca1\u8fc7\u5173\uff0c\u6709\u8bba\u6587\u539f\u7a3f\u4e0d\u8868\u793a\u62ff\u5230\u535a\u58eb\u5b66\u4f4d\u201d\u3002\u201c\u6df1\u7eff\u201d\u4e3b\u6301\u4eba\u5f6d\u6587\u6b63\u8868\u793a\uff0c\u8521\u82f1\u6587\u4e3a\u4e86\u5706\u8c0e\u800c\u4e0d\u65ad\u7f16\u7ec7\u8c0e\u8a00\uff0c\u4f26\u6566\u5927\u5b66\u56fe\u4e66\u9986\u5728\u6536\u85cf\u7684\u8be5\u672c\u7ffb\u5370\u672c\u4e2d\u9644\u6ce8\u201c\u8fd9\u662f2019\u5e74\u6765\u81ea\u8521\u82f1\u6587\u7684\u4e2a\u4eba\u7248\u672c\u201d\uff0c\u800c\u975e\u201c\u8fd9\u662f1984\u5e74\u539f\u4ef6\u7684\u5f71\u672c\u201d\u3002\u5a92\u4f53\u4eba\u6768\u5baa\u5b8f\u8868\u793a\uff0c\u201c\u8521\u82f1\u6587\u5199\u7684\u4e1c\u897f\u201d\u5728\u4f26\u6566\u653f\u7ecf\u5b66\u9662\u88ab\u653e\u5728\u201c\u4e66\u7c4d\u7c7b\u201d\uff0c\u800c\u5176\u4ed6\u4eba\u7684\u8bba\u6587\u5219\u653e\u5728\u201c\u8bba\u6587\u7c7b\u201d\uff0c\u8fd9\u5f88\u5947\u602a\u3002</p>\n<p align=\"justify\"><strong>\u3000\u3000\u5e74\u4ee3\u611f\u7684\u6280\u672f\u95ee\u9898\uff1a\u90a3\u4e9b\u5e74\u505a\u5f97\u5230\u5de6\u53f3\u5bf9\u9f50\u7684\u7535\u8111\u6392\u7248\u5417\uff1f</strong></p>\n<p align=\"justify\">\u3000\u3000\u6280\u672f\u95ee\u9898\u96be\u4ee5\u81ea\u5706\u5176\u8bf4\uff0c\u4ee4\u4eba\u4ea7\u751f\u201c\u7a7f\u8d8a\u201d\u4e4b\u611f\u3002\u201c\u5e9c\u201d\u53d1\u8a00\u4eba\u79f0\uff0c\u201c36\u5e74\u524d\u5199\u8bba\u6587\uff0c\u9760\u7684\u662f\u6253\u5b57\u673a\u800c\u4e0d\u662f\u7535\u8111\u201d\uff0c\u6545\u8bba\u6587\u683c\u5f0f\u4e0d\u4e00\u4e14\u6709\u624b\u5199\u4fee\u6539\u7684\u75d5\u8ff9\u3002\u7136\u800c\uff0c\u6574\u7bc7\u8bba\u658790%\u662f\u7528\u5de6\u8fb9\u5bf9\u9f50\u3001\u53f3\u8fb9\u5bf9\u9f50\u7684\u6253\u6cd5\uff0c\u4e5f\u5c31\u662f334\u9875\u90fd\u4f7f\u7528\u8f6f\u4f53\u6392\u7248\u3002\u636e\u7f51\u7edc\u67e5\u8bc1\uff0c\u8fd9\u79cd\u5de6\u53f3\u5bf9\u9f50\u7684\u6253\u6cd5\u662f1990\u5e74\u521d\u53d1\u660e\u7684\u3002\u90a3\u8521\u82f1\u65871984\u5e74\u7684\u535a\u58eb\u8bba\u6587\u5982\u4f55\u72ec\u5bb6\u53d6\u5f97\u672a\u6765\u65b0\u79d1\u6280\u5462\uff1f</p>\n<p align=\"justify\">\u3000<strong>\u3000\u6709\u6761\u4ef6\u7684\u903b\u8f91\u95ee\u9898\uff1a\u771f\u5982\u8521\u6240\u8a00\u201c\u6709\u5b66\u4f4d\u5c31\u4e00\u5b9a\u6709\u8bba\u6587\u201d\u5417\uff1f</strong></p>\n<p align=\"justify\">\u3000\u3000\u4f17\u6240\u5468\u77e5\uff0c\u535a\u58eb\u8bba\u6587\u5fc5\u7ecf\u91cd\u91cd\u5ba1\u6838\u3001\u5173\u5173\u901a\u8fc7\u624d\u80fd\u5408\u683c\uff0c\u624d\u6709\u8d44\u683c\u83b7\u53d6\u5b66\u4f4d\u3002\u6309\u5e38\u7406\uff0c\u4f46\u51e1\u53d6\u5f97\u535a\u58eb\u5b66\u4f4d\uff0c\u5fc5\u5b9a\u8bba\u6587\u5408\u683c\uff0c\u4e8c\u8005\u770b\u4f3c\u5145\u5206\u5fc5\u8981\u6761\u4ef6\u3002\u53ef\u8521\u82f1\u6587\u7684\u8bba\u6587\u975e\u6bd4\u5bfb\u5e38\uff0c\u82f1\u56fd\u4e09\u5927\u5b66\u672f\u673a\u6784\u7eb7\u7eb7\u201c\u641e\u4e22\u201d\uff0c106\u4f4d\u540c\u671f\u751f\u90fd\u6709\u8bba\u6587\u6536\u85cf\u8bb0\u5f55\u800c\u8521\u662f\u552f\u4e00\u4f8b\u5916\uff0c\u67e5\u9605\u5176\u6587\u9700\u8d85\u5e38\u89c4\u4e25\u683c\u7a0b\u5e8f\uff0c\u5728\u4e16\u53e6\u4e24\u540d\u5bfc\u5e08\u56e0\u4e0d\u660e\u539f\u56e0\u4e0d\u51c6\u516c\u5f00\u2026\u2026\u5982\u6b64\u7591\u70b9\u91cd\u91cd\uff0c\u8bba\u6587\u5982\u4f55\u901a\u8fc7\uff1f\u6709\u4eba\u8d28\u7591\uff0c\u4e0d\u6392\u9664\u8521\u82f1\u6587\u91c7\u53d6\u672a\u77e5\u624b\u6bb5\u53d6\u5f97\u5b66\u4f4d\uff0c\u5b9e\u5219\u8bba\u6587\u4e0d\u591f\u8d44\u683c\u3002</p>\n<p align=\"justify\">\u3000<strong>\u3000\u4e00\u76f4\u5728\u7684\u8bda\u4fe1\u95ee\u9898\uff1a\u8521\u82f1\u6587,\u6587\u5982\u5176\u4eba\u3002</strong></p>\n<p align=\"justify\">\u3000\u3000\u5e38\u8a00\u9053\uff0c\u6587\u5982\u5176\u4eba\u3002\u8521\u82f1\u6587\u7684\u8bba\u6587\u771f\u4f2a\u65ad\u65ad\u7eed\u7eed\u88ab\u8d28\u7591\u591a\u5e74\uff0c\u4e00\u5982\u5176\u6267\u653f\u4e09\u5e74\u6765\uff0c\u8bda\u4fe1\u95ee\u9898\u4e00\u76f4\u5907\u53d7\u8bdf\u75c5\u3002\u201c\u8521\u82f1\u6587\u7684\u5341\u5927\u2018\u53d1\u5939\u5f2f\u2019\u201d\u201c\u8521\u82f1\u6587\u4e0a\u53f0\u524dvs.\u4e0a\u53f0\u540e\u201d\u2026\u2026\u8fd9\u90fd\u6210\u4e3a\u5c9b\u5185\u4e00\u4e9b\u5a92\u4f53\u6df1\u5177\u4ee3\u8868\u6027\u7684\u6587\u7ae0\u6807\u9898\u3002\u8981\u8521\u82f1\u6587\u5151\u73b0\u81ea\u5df1\u8bb8\u4e0b\u7684\u627f\u8bfa\uff0c\u4eff\u4f5b\u5df2\u6210\u4e3a\u4e0d\u53ef\u80fd\u7684\u4efb\u52a1\u3002</p>\n<p align=\"justify\">\u3000\u3000\u95fb\u4e0d\u5230\u8bba\u6587\u9999\uff0c\u89e3\u4e0d\u4e86\u4e16\u4eba\u7591\uff0c\u5bf9\u4e8e\u8521\u82f1\u6587\uff0c\u6211\u4eec\u663e\u800c\u6613\u89c1\u7684\u5374\u662f\u8bda\u4fe1\u5168\u65e0\uff0c\u6587\u5982\u5176\u4eba\u3002\uff08\u4e2d\u56fd\u53f0\u6e7e\u7f51\u7f51\u53cb\uff1a\u9ad8\u98de\uff09</p>\n<p align=\"right\">\uff08\u672c\u6587\u4e3a\u6295\u7a3f\u4f5c\u54c1\uff0c\u4e0d\u4ee3\u8868\u4e2d\u56fd\u53f0\u6e7e\u7f51\u89c2\u70b9\uff09</p></div>\n  </div>\n  <p class=\"editor\">[\u8d23\u4efb\u7f16\u8f91\uff1a\u8d75\u9759]</p>\n  <!--\u7ffb\u9875\u9875\u7801_begin-->\n  <div id=\"pageNumber\">\n    <script type=\"text/javascript\">\n      createPageHTML(1, 0, \"t20191010_12208159\", \"htm\");\n    </script>\n  </div>\n  <!--\u7ffb\u9875\u9875\u7801_end-->\n  <!--\u76f8\u5173\u5185\u5bb9_begin-->\n  <div id=\"relatedInfo\">\n    <h3>\u76f8\u5173\u5185\u5bb9</h3>\n    <ul>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/plzhx/plyzl/201910/t20191009_12207993.htm\" target=\"_blank\" title=\"\u4e8e\u5f3a\uff1a\u8521\u82f1\u6587\u7684\u201c\u8bba\u6587\u7591\u4e91\u201d\u5df2\u88ab\u67d3\u6210\u4e86\u58a8\u7eff\u8272\">\u4e8e\u5f3a\uff1a\u8521\u82f1\u6587\u7684\u201c\u8bba\u6587\u7591\u4e91\u201d\u5df2\u88ab\u67d3\u6210\u4e86\u58a8\u7eff\u8272</a></li>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/plzhx/plyzl/201909/t20190929_12206005.htm\" target=\"_blank\" title=\"\u8bf7\u8521\u82f1\u6587\u6bd4\u7167\u897f\u65b9\u6807\u51c6\u8bda\u5b9e\u9762\u5bf9\u201c\u8bba\u6587\u4e89\u8bae\u201d\">\u8bf7\u8521\u82f1\u6587\u6bd4\u7167\u897f\u65b9\u6807\u51c6\u8bda\u5b9e\u9762\u5bf9\u201c\u8bba\u6587\u4e89\u8bae\u201d</a></li>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/plzhx/plyzl/201909/t20190927_12205878.htm\" target=\"_blank\" title=\"\u8521\u82f1\u6587\u201c\u8bba\u6587\u95e8\u201d\u6709\u4f55\u9690\u60c5\uff1f\u53ef\u80fd\u5c31\u5728\u5904\u7406\u7a0b\u5e8f\u91cc\">\u8521\u82f1\u6587\u201c\u8bba\u6587\u95e8\u201d\u6709\u4f55\u9690\u60c5\uff1f\u53ef\u80fd\u5c31\u5728\u5904\u7406\u7a0b\u5e8f\u91cc</a></li>\n\t \n\t  <li><a href=\"http://www.taiwan.cn/plzhx/plyz